fun findMarker(input: String, size: Int): Int {
    val windows = input.asSequence().windowed(size)
    for(i in 0 until windows.count()) {
        if (windows.elementAt(i).distinct().size == size) {
            return i + size
        }
    }
    return -1
}

const val startPacketSize = 4
const val startMessageSize = 14

fun main() {

    assert(findMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb", startPacketSize) == 7)
    assert(findMarker("bvwbjplbgvbhsrlpgdmjqwftvncz", startPacketSize) == 5)
    assert(findMarker("nppdvjthqldpwncqszvftbrmjlhg", startPacketSize) == 6)
    assert(findMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", startPacketSize) == 10)
    assert(findMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", startPacketSize) == 11)
    println(findMarker("", startPacketSize))

    assert(findMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb", startMessageSize) == 19)
    assert(findMarker("bvwbjplbgvbhsrlpgdmjqwftvncz", startMessageSize) == 23)
    assert(findMarker("nppdvjthqldpwncqszvftbrmjlhg", startMessageSize) == 23)
    assert(findMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", startMessageSize) == 29)
    assert(findMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", startMessageSize) == 26)
    println(findMarker("", startMessageSize))

}